import React , {useState} from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styles from './CustomerForm.module.css';
import axios from 'axios';
import { TextField, Button, Box, Typography, Select, MenuItem, InputLabel, FormControl } from '@mui/material';

interface CustomerFormInputs {
    name: string;
    email: string;
    tel: number;
    address: string;
    company: string;
}

const CustomerCreate: React.FC = () => {
    const { register, handleSubmit, reset } = useForm<CustomerFormInputs>();
    const [companies, setCompanies] = useState<string[]>(["Company1", "Company2"]);
    const onSubmit: SubmitHandler<CustomerFormInputs> = async (data) => {
        try {
            await axios.post('http://localhost:3001/api/users', data);
            alert('顧客が登録されました');
            reset();
        } catch (error) {
            console.error('登録に失敗しました:', error);
            alert('顧客の登録に失敗しました');
        }
    };
    // const onSubmit: SubmitHandler<CustomerFormInputs> = (data) => {
    //     console.log(data); 
    //     alert('顧客が登録されました');
    //     reset();
    // };

    return (
        <div className={styles.customerForm}>
            <Typography variant="h5" gutterBottom>顧客登録</Typography>
            <form>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                    <TextField
                        label="名前"
                        fullWidth
                        {...register("name", { required: "名前は必須です" })}
                        error={false}
                        helperText=""
                    />
                    <TextField
                        label="メール"
                        type="email"
                        fullWidth
                        {...register("email", { required: "メールは必須です" })}
                        error={false}
                        helperText=""
                    />
                    <TextField
                        label="電話番号"
                        fullWidth
                        {...register("tel", { required: "電話番号は必須です" })}
                        error={false}
                        helperText=""
                    />
                    <TextField
                        label="住所"
                        fullWidth
                        {...register("address", { required: "住所は必須です" })}
                        error={false}
                        helperText=""
                    />
                    <FormControl fullWidth margin="normal">
                    <InputLabel>会社名</InputLabel>
                    <Select {...register("company", { required: true })} defaultValue="" label="会社">
                        {companies.map((company, index) => (
                            <MenuItem key={index} value={company}>{company}</MenuItem>
                        ))}
                    </Select>
                    </FormControl>
                    <Button variant="contained" color="primary" type="submit" fullWidth>
                        登録
                    </Button>
                </Box>
            </form>
        </div>
    );
};

export default CustomerCreate;
