import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Home: React.FC = () => {

  interface TestestesData {
    id: number;
    name: string;
  }

  const [data, setData] = useState<TestestesData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

    useEffect(() => {
      const fetchData = async () => {
        try {
          const response = await axios.get<TestestesData[]>('http://localhost:3001/api/testestes'); // バックエンドAPIにリクエスト
          setData(response.data); 
          setLoading(false);
        } catch (err) {
          setError('データの取得に失敗しました');
          setLoading(false);
        }
      };

      fetchData();
    }, []);

    if (loading) {
      return <div>Loading...</div>;
    }

    if (error) {
      return <div>{error}</div>;
    }

  return (
    <div>
        <h1>顧客管理システム</h1>
          <ul>
            {data.map(item => (
              <li key={item.id}>
                {item.name}
              </li>
            ))}
          </ul>
    </div>
  );
};

export default Home;
