import React, { useEffect, useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import axios from 'axios';
import Header from './components/Header';
import Footer from './components/Footer';

// データの型定義
interface TestestesData {
  id: number;
  name: string;
}

const App: React.FC = () => {
  const [data, setData] = useState<TestestesData[]>([]);
  const [loading, setLoading] = useState<boolean>(true);
  const [error, setError] = useState<string | null>(null);

  // データを取得するためのuseEffect
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get<TestestesData[]>('http://localhost:3001/api/testestes'); // バックエンドAPIにリクエスト
        setData(response.data); 
        setLoading(false);
      } catch (err) {
        setError('データの取得に失敗しました');
        setLoading(false);
      }
    };

    fetchData();
  }, []);

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <Router>
    <Header />
    <div>
      <h1>テストデータ</h1>
      <ul>
        {data.map((item) => (
          <li key={item.id}>
            {item.name}
          </li>
        ))}
      </ul>
    </div>

    </Router>
  );
};

export default App;
