import React, { useState, useEffect } from 'react';
import styles from './CustomerList.module.css';
import { Modal, Box, Typography, Button, Select, MenuItem, FormControl, InputLabel} from '@mui/material';

interface Customer {
    id: number;
    name: string;
    email: string;
    tel: string;
    // company: string;
}

interface Company {
    company: string;
}

const mockCustomers: Customer[] = [
    { id: 10, name: "aaa", email: "aaaa@aaaa.com", tel: "123-456-7890"},
];

const CustomerList: React.FC = () => {
    const [customers, setCustomers] = useState<Customer[]>([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        setCustomers(mockCustomers);
    }, []);

    const filteredCustomers = customers.filter((customer) =>
        customer.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className={styles.customerList}>
            <div className={styles.customerListHeader}>
                <h2>顧客一覧</h2>
                <Button className={styles.modalButton} onClick={() => setIsModalOpen(true)}>絞り込み条件</Button>
            </div>

            {/* <input
                type="text"
                placeholder="検索"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Button>検索</Button> */}
            <ul>
                {filteredCustomers.map((customer) => (
                    <li key={customer.id}>
                        {customer.name} - {customer.email} - {customer.tel}
                    </li>
                ))}
            </ul>

            <Modal open={isModalOpen} onClose={() => setIsModalOpen(false)}>
                <Box className={styles.modalOverlay}>
                    <Box className={styles.modalContent}>
                        <Typography variant="h6" className={styles.modalHeader}>
                            条件
                            <Button className={styles.closeButton} onClick={() => setIsModalOpen(false)}>
                                閉じる
                            </Button>
                        </Typography>
                        {/* 顧客名検索フィールド */}
                        <TextField
                            label="顧客名で検索"
                            variant="outlined"
                            fullWidth
                            value={searchQuery}
                            onChange={(e) => setSearchQuery(e.target.value)}
                            sx={{ mb: 2 }}
                        />

                        {/* 会社名セレクトボックス */}
                        <FormControl fullWidth sx={{ mb: 2 }}>
                            <InputLabel>会社名</InputLabel>
                            <Select
                                value={selectedCompany}
                                onChange={handleCompanyChange}
                                displayEmpty
                            >
                                {/* <MenuItem value="">全ての会社</MenuItem>
                                {companies.map((company, index) => (
                                    <MenuItem key={index} value={company}>
                                        {company}
                                    </MenuItem>
                                ))} */}
                            </Select>
                        </FormControl>
                        <ul>
                            <li>イースマイル</li>
                            <li>エンジニアリング</li>
                        </ul>
                    </Box>
                </Box>
            </Modal>
        </div>
    );
};

export default CustomerList;
