import React, { useState, useEffect } from 'react';
import styles from './CustomerList.module.css';
import { Modal, Box, Typography, Button } from '@mui/material';

interface Customer {
    id: number;
    name: string;
    email: string;
    tel: string;
    company: string;
}

const mockCustomers: Customer[] = [
    { id: 10, name: "aaa", email: "aaaa@aaaa.com", tel: "123-456-7890", company: "Company" },
];

const CustomerList: React.FC = () => {
    const [customers, setCustomers] = useState<Customer[]>([]);
    const [searchQuery, setSearchQuery] = useState("");
    const [isModalOpen, setIsModalOpen] = useState(false);

    useEffect(() => {
        setCustomers(mockCustomers);
    }, []);

    const filteredCustomers = customers.filter((customer) =>
        customer.name.toLowerCase().includes(searchQuery.toLowerCase())
    );

    return (
        <div className={styles.customerList}>
            <div className={styles.customerListHeader}>
                <h2>顧客一覧</h2>
                <Button className={styles.modalButton} onClick={() => setIsModalOpen(true)}>会社一覧</Button>
            </div>

            <input
                type="text"
                placeholder="検索"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
            />
            <Button>検索</Button>
            <ul>
                {filteredCustomers.map((customer) => (
                    <li key={customer.id}>
                        {customer.name} - {customer.email} - {customer.tel} - {customer.company}
                    </li>
                ))}
            </ul>

            <Modal open={isModalOpen} onClose={() => setIsModalOpen(false)}>
                <Box>
                    <Typography component="h2">
                        会社一覧
                    </Typography>
                    <ul>
                        <li>Company A</li>
                        <li>Company B</li>
                    </ul>
                    <Button onClick={() => setIsModalOpen(false)} sx={{ mt: 2 }}>
                        閉じる
                    </Button>
                </Box>
            </Modal>
        </div>
    );
};

export default CustomerList;
