import React , {useState} from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styles from './CustomerForm.module.css';
import axios from 'axios';
import { TextField, Button, Typography, Select, MenuItem, InputLabel, FormControl } from '@mui/material';

interface CustomerFormInputs {
    name: string;
    email: string;
    tel: number;
    address: string;
    company: string;
}

const CustomerCreate: React.FC = () => {
    const { register, handleSubmit, reset } = useForm<CustomerFormInputs>();
    const [companies, setCompanies] = useState<string[]>(["esmile"]);
    const onSubmit: SubmitHandler<CustomerFormInputs> = async (data) => {
        try {
            await axios.post('http://localhost:3001/api/users', data);
            alert('顧客が登録されました');
            reset();
        } catch (error) {
            console.log(data);
            console.error('登録に失敗しました:', error);
            alert('顧客の登録に失敗しました');
        }
    };

    return (
        <div className={styles.customerForm}>
            <Typography variant="h5" gutterBottom>顧客登録</Typography>
            <form onSubmit={handleSubmit(onSubmit)}>
                <TextField
                    label="名前"
                    {...register("name", { required: true })}
                    fullWidth
                    margin="normal"
                />

                <TextField
                    label="メール"
                    type="email"
                    {...register("email", { required: true })}
                    fullWidth
                    margin="normal"
                />

                <TextField
                    label="電話番号"
                    {...register("tel", { required: true })}
                    fullWidth
                    margin="normal"
                />

                <TextField
                    label="住所"
                    {...register("address", { required: true })}
                    fullWidth
                    margin="normal"
                />

                <FormControl fullWidth margin="normal">
                    <InputLabel>会社</InputLabel>
                    <Select {...register("company", { required: true })} defaultValue="" label="会社">
                        {companies.map((company, index) => (
                            <MenuItem key={index} value={company}>{company}</MenuItem>
                        ))}
                    </Select>
                </FormControl>

                <Button type="submit" fullWidth variant="contained" color="primary">
                    登録
                </Button>
            </form>
        </div>
    );
};

export default CustomerCreate;
