import React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import './CompanyForm.module.css';

interface CompanyFormInputs {
    name: string;
    address: string;
    tel: string;
}

const CompanyCreate: React.FC = () => {
    const { register, handleSubmit, reset } = useForm<CompanyFormInputs>();

    const onSubmit: SubmitHandler<CompanyFormInputs> = (data) => {
        console.log(data); 
        alert('会社が登録されました');
        reset();
    };

    return (
        <div className='{styles.customerCreate}'>
            <h2>会社登録</h2>
            <form onSubmit={handleSubmit(onSubmit)}>
                <label>会社名</label>
                <input {...register("name", { required: true })} />

                <label>住所</label>
                <input {...register("address", { required: true })} />

                <label>電話番号</label>
                <input {...register("tel", { required: true })} />

                <button type="submit">登録</button>
            </form>
        </div>
    );
};

export default CompanyCreate;
