import React from 'react';
import { useForm, SubmitHandler } from 'react-hook-form';
import styles from './CustomerForm.module.css';
import { TextField, Button, Box, Typography } from '@mui/material';

interface CustomerFormInputs {
    name: string;
    email: string;
    tel: number;
    adress: string;
    company: string;
}

const CustomerCreate: React.FC = () => {
    const { register, handleSubmit, reset } = useForm<CustomerFormInputs>();

    const onSubmit: SubmitHandler<CustomerFormInputs> = (data) => {
        console.log(data); 
        alert('顧客が登録されました');
        reset();
    };

    return (
        <div className={styles.customerForm}>
            <Typography variant="h5" gutterBottom>顧客登録</Typography>
            <div className={styles.customerForm}>
            <Typography variant="h5" gutterBottom>顧客登録</Typography>
            <form onSubmit={handleSubmit(onSubmit)}>
                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 2 }}>
                    <TextField
                        label="名前"
                        fullWidth
                        {...register("name", { required: "名前は必須です" })}
                        error={false}
                        helperText=""
                    />
                    <TextField
                        label="メール"
                        type="email"
                        fullWidth
                        {...register("email", { required: "メールは必須です" })}
                        error={false}
                        helperText=""
                    />
                    <TextField
                        label="電話番号"
                        fullWidth
                        {...register("tel", { required: "電話番号は必須です" })}
                        error={false}
                        helperText=""
                    />
                    <TextField
                        label="住所"
                        fullWidth
                        {...register("address", { required: "住所は必須です" })}
                        error={false}
                        helperText=""
                    />
                    <TextField
                        label="会社名"
                        fullWidth
                        {...register("company", { required: "会社名は必須です" })}
                        error={false}
                        helperText=""
                    />
                    <Button variant="contained" color="primary" type="submit" fullWidth>
                        登録
                    </Button>
                </Box>
            </form>
        </div>
        </div>
    );
};

export default CustomerCreate;
